mapper.select:
 		SELECT * /*+ INDEX(TBL_ROOM IDX_ROOM) */
		FROM TBL_ROOM
		WHERE ROOMNUM > #{cri.lastRoomNum}
		AND ROWNUM < = #{cri.amount}
		AND PENSIONID = #{pensionid}

mapper.update:
 		UPDATE TBL_ROOM SET
		DEADLINE = #{deadline},
		PRICE = #{price},
		ROOMNAME = #{roomName}
 		WHERE ROOMNUM = #{roomNum}
